mapper.select:

SELECT * FROM
( SELECT ROWNUM R, D.* FROM
( SELECT * FROM REPLY_JAVASCRIPT ORDER BY reply_ID DESC) D ) B
WHERE B.R
BETWEEN #{startRow} AND #{endRow}
and post_id =#{post_id}

mapper.update:
update JAVASCRIPT set
post_contents = #{post_contents}, post_subject = #{post_subject}
where post_id = #{post_id}
